  newValue() {
    const newTestArray = [];
    const headerArray = [{ text: 'Item', style: 'tableHeaderRow' },
    { text: 'Description', style: 'tableHeaderRow' },
    { text: 'Quantity', style: 'tableHeaderRow' }    , {
      text: 'Discount (%)',
      style: 'tableHeaderRow'
    }, { text: 'Price', style: 'tableHeaderTotal' },
    { text: 'Total', style: 'tableHeaderTotal' }];
    newTestArray.push(headerArray);
    for (let i = 0; i < this.workOrderReq.length; i++) {
      newTestArray.push([{ text: this.workOrderReq[i].item, style: 'rowStyle' },
      { text: this.workOrderReq[i].description, style: 'rowStyle' },
      { text: this.workOrderReq[i].quantity, style: 'rowStyle' },
      { text: this.workOrderReq[i].discount, style: 'rowStyle' },
      { text: this.workOrderReq[i].price.toFixed(2), style: 'rowTotal', },
      { text: this.workOrderReq[i].total.toFixed(2), style: 'rowTotal' }]);
    }
    return newTestArray;
  }
  discountNull() {
    this.totalAmountWithDiscount = 0;
    const newTestArray = [];
    const headerArray = [{ text: 'Item', style: 'tableHeaderRow' },
    { text: 'Description', style: 'tableHeaderRow' },
    { text: 'Quantity', style: 'tableHeaderRow' },
    { text: 'Price', style: 'tableHeaderTotal' },
    { text: 'Total', style: 'tableHeaderTotal' }];
    newTestArray.push(headerArray);
    for (let i = 0; i < this.workOrderReq.length; i++) {
      const qty = this.workOrderReq[i].quantity;
      const price = this.workOrderReq[i].price;
      const priceResult = qty * price;
      newTestArray.push([{ text: this.workOrderReq[i].item, style: 'rowStyle' },
      { text: this.workOrderReq[i].description, style: 'rowStyle' },
      { text: this.workOrderReq[i].quantity, style: 'rowStyle' },
      { text: this.workOrderReq[i].price.toFixed(2), style: 'rowTotal' },
      { text: priceResult.toFixed(2), style: 'rowTotal' }]);
      this.totalAmountWithDiscount += this.workOrderReq[i].quantity * this.workOrderReq[i].price;
    }
    return newTestArray;
  }
  totalAmountIn() {
    this.workPrice = 0;
    for (let i = 0; i < this.workOrderReq.length; i++) {
      this.workPrice += this.workOrderReq[i].quantity * this.workOrderReq[i].price;
    }
  }
  viewSingleWorkOrderPdf(work, customer, company, temp) {
    this.workOrder = work;
    if (!this.selectedValue) {
      this.selectTermsCondition = true;
    } else {
      this.selectTermsCondition = false;
      this.workOrder = work;
      this.workOrderReq = work[0].requirements;
      this.customerData = customer;
      this.companyData = company;
      this.workOrderPdf = company;
      if (temp === 'With Discount + GST') {
        this.pdfWithDiscount();
      } else if (temp === 'Without Discount + GST') {
        this.totalAmountIn();
        this.pdfWithoutDiscount();
      }
    }
  }
  selectTerms(e, workOrderPdf) {
    if (e.source.checked) {
      this.selectedValue = e.value;
      this.selectTermsCondition = false;
      switch (this.selectedValue) {
        case (this.terms[0].term): {
          this.selectedTermsText = [];
          this.selectedTermsPdf = [];
          break;
        }
        case (this.terms[1].term): {
          this.selectedTermsText = ['Production Terms & Condition : '];
          this.selectedTermsPdf = workOrderPdf[0].terms.split(',');
          break;
        }
        case (this.terms[2].term): {
          this.selectedTermsText = ['Digital Marketing Terms & Condition : '];
          this.selectedTermsPdf = workOrderPdf[0].digitalterms.split(',');
          break;
        }
      }
    } else {
      this.selectTermsCondition = true;
    }
  }
